  return (
    <div>
      {/* {greeting} */}
      
        <input
            type="text"
            value={inputData}
            onChange={e => setInputData(e.target.value)}
        />
        <button onClick={sendDataToBackend}>Send to Backend</button>
        {result && <p>Processed Result: {result}</p>}
        
    </div>
  );
}

export default Index;
